num1=eval(input("enter the number first"))
num2=eval(input("enter the number second"))
oper=input("enter the operator('+','-','*','/')"  )
if oper =='+':
    print(num1+num2)
elif oper== '-':
    print(num1-num2)
elif oper== '*':
    print(num1*num2)
elif oper == '/':
    print(num1/num2)
else:
    print("wrong operator")
